function para(t){var n=Math.abs(t);return n=Math.pow(1.04,n),n*=-1,n+=52,n<30&&(n=30),n}var toImage=function(t){var n;return html2canvas($(t),{onrendered:function(t){n=t.toDataURL("image/png",1)}}),n};$(document).ready(function(){$(".text").keyup(function(){var t=para($("#tp_txt").text().length),n=para($("#bt_txt").text().length);$("#tp_txt").css("font-size",t),$("#bt_txt").css("font-size",n)}),$("#download").click(function(){var t=toImage(".image");document.body.getElementById("#img_out").src=t})});